INSERT INTO `ecs_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `is_shipping`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `sales_volume`, `comments_number`, `bb_chicun`, `mobile_desc`, `fencheng`, `sales_count`, `goods_shipai`, `sales_volume_base` ) VALUES  ('61', '11', 'ECS000061', '海南三亚 - 蜈支洲 - 南山 5 日游 ', '+', '2', '15', '', '9999', '0.000', '3478.79', '2899.00', '0.00', '0', '0', '1', '', '', '<p>&nbsp;<span style=\"font-size: 20px; font-weight: bold; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;;\">出发地</span><span style=\"font-size: 20px; font-weight: bold; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;;\">三亚</span></p>\r\n<div id=\"J_JourneyDay1\" style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;; font-size: 14px;\">\r\n<div class=\"detail-journey-title\" style=\"margin: 15px 0px 10px; padding: 0px; position: relative; font-size: 18px; color: rgb(17, 17, 17);\">行程安排：抵达三亚</div>\r\n<div class=\"detail-journey-desc\" style=\"margin: 0px; padding: 0px; position: relative; color: rgb(102, 102, 102);\">☆今日行程：拾起行囊，备好装备，出发去海南啦！<br />\r\n终于来到了梦寐以求的度假圣地&mdash;三亚。接机送往酒店。</div>\r\n<div class=\"detail-journey-seprate\" style=\"margin: 10px 0px; padding: 0px; border-width: 1px 0px 0px; border-style: dotted; border-right-color: initial; border-bottom-color: initial; border-left-color: initial; border-image: initial; border-top-color: rgb(221, 221, 221);\">&nbsp;</div>\r\n<div class=\"detail-journey-title\" style=\"margin: 15px 0px 10px; padding: 0px; position: relative; font-size: 18px; color: rgb(17, 17, 17);\">★温馨提醒</div>\r\n<div class=\"detail-journey-desc\" style=\"margin: 0px; padding: 0px; position: relative; color: rgb(102, 102, 102);\">①即日起预定本线路，均享受专车接机服务（一般为 5 座小轿车或商务车）。目前三亚凤凰机场国内航站楼停车场实施封闭式改造施工，因此机场启用了临时停车场，游客统一由 A 出口处出来乘坐机场免费摆渡车前往临时停车场（乘车时间约十分钟）。摆渡车辆根据客流量来发车，人少车等、人满车开，候车时间不定，届时抵达机场后需要稍作等待，（一般不超过 60 分钟）请您谅解。<br />\r\n②现部分机场有人会向您推销保险，遇此推销请谨慎购买。请您了解在途牛所买保险项，我们会按照合同承诺帮您办理好各项保险等附加服务，绝不会泄露您的身份证或电话等信息！<br />\r\n③海南是热带城市，这个季节湿度较大，特别是临海的酒店客房有可能会有一些潮味，甚至是霉味，请您不必担心，空调打开 10 分钟通风后，味道即可散去。如果较为介意，您可以及时联系导游或者酒店工作人员调换房间。<br />\r\n④订单默认安排标间，如您需要大床，需要和客服确认申请，不保证安排，实际以当天入住为准</div>\r\n<div class=\"detail-journey-seprate\" style=\"margin: 10px 0px; padding: 0px; border-width: 1px 0px 0px; border-style: dotted; border-right-color: initial; border-bottom-color: initial; border-left-color: initial; border-image: initial; border-top-color: rgb(221, 221, 221);\">&nbsp;</div>\r\n<div class=\"detail-journey-title\" style=\"margin: 15px 0px 10px; padding: 0px; position: relative; font-size: 18px; color: rgb(17, 17, 17);\">用餐：全天不含餐</div>\r\n<div class=\"detail-journey-seprate\" style=\"margin: 10px 0px; padding: 0px; border-width: 1px 0px 0px; border-style: dotted; border-right-color: initial; border-bottom-color: initial; border-left-color: initial; border-image: initial; border-top-color: rgb(221, 221, 221);\">&nbsp;</div>\r\n<div class=\"detail-journey-title\" style=\"margin: 15px 0px 10px; padding: 0px; position: relative; font-size: 18px; color: rgb(17, 17, 17);\">★可选酒店一：三亚福朋喜来登酒店 Four Points by Sheraton Hainan Sanya</div>\r\n<div class=\"detail-journey-desc\" style=\"margin: 0px; padding: 0px; position: relative; color: rgb(102, 102, 102);\">【三亚福朋喜来登酒店】酒店有着 &ldquo;东方夏威夷帆船酒店&rdquo; 之美誉，由喜达屋酒店与度假村国际集团管理的商务度假型酒店，拥有现代化设施的宽敞客房与套房共三百余间，房内拥入福朋舒适之床，让您和家人尽享一夜酣眠。<br />\r\n位置：酒店位于三亚湾路，临近迎宾路；自酒店步行即可到购物中心、夜市等，出行便捷。<br />\r\n设施：酒店内有配设齐全的休闲娱乐场所，有网球场、健身室、儿童乐园等